import {
  DATA_REQUESTED, DATA_LOADED, API_ERRORED,
} from './actions';

const rootReducer = (
  state = { data: {}, error: null, loading: false },
  { type, data, error },
) => {
  switch (type) {
    case DATA_REQUESTED:
      return { ...state, error: null, loading: true };
    case DATA_LOADED:
      return {
        ...state, data, error: null, loading: false,
      };
    case API_ERRORED:
      return {
        ...state, data: {}, error, loading: false,
      };
    default:
      return state;
  }
};

export default rootReducer;
