import { takeEvery, call, put } from 'redux-saga/effects';
import axios from 'axios';
import { DATA_REQUESTED, receiveDataSuccess, receiveDataFailure } from './actions';

function* onFetchUsers() {
  try {
    // API Request
    const response = yield call(axios.get, ['https://jsonplaceholder.typicode.com/posts']);
    yield put(receiveDataSuccess(response.data[0]));
  } catch (e) {
    yield put(receiveDataFailure(e));
  }
}

export function* fetchUserSaga() {
  yield takeEvery(DATA_REQUESTED, onFetchUsers);
}
